use strict;my $hasla = $ARGV[0];die "Uzycie:\n==============================================\nperl $0 plik_z_haszami.csv < plik_z_userami.csv > output.csv\n" if $ARGV[0] =~ //;while(<STDIN>) {	my $curline = $_;	my ($n, $m, $h) = split(/;/, $curline, 3);	$h =~ s/\n|\r//ig;	my $szuk = `cat $hasla | grep '^$h'`;	if($szuk =~ //) {		print $n, ';', $m, ';', $szuk;	} else {		print $n, ';', $m, ';', $h, "\n";	}}